import React, { useState, useEffect, useRef } from 'react';

const BackgroundMusic = () => {
  const [isPlaying, setIsPlaying] = useState(false);
  const [volume, setVolume] = useState(0.5);
  const [player, setPlayer] = useState(null);
  const [isReady, setIsReady] = useState(false);
  const [error, setError] = useState(null);
  const playerRef = useRef(null);
  const initializationRef = useRef(false);

  useEffect(() => {
    // Prevent multiple initializations
    if (initializationRef.current) return;
    initializationRef.current = true;

    // Load YouTube API
    const loadYouTubeAPI = () => {
      return new Promise((resolve, reject) => {
        if (window.YT && window.YT.Player) {
          resolve();
          return;
        }

        // Set up the callback before loading script
        window.onYouTubeIframeAPIReady = () => {
          resolve();
        };

        if (!document.querySelector('script[src*="youtube"]')) {
          const script = document.createElement('script');
          script.src = 'https://www.youtube.com/iframe_api';
          script.async = true;
          script.onerror = () => reject(new Error('Failed to load YouTube API'));
          document.head.appendChild(script);
        } else {
          // Script already exists, wait for it to load
          const checkAPI = () => {
            if (window.YT && window.YT.Player) {
              resolve();
            } else {
              setTimeout(checkAPI, 100);
            }
          };
          checkAPI();
        }
      });
    };

    const initializePlayer = async () => {
      try {
        await loadYouTubeAPI();

        if (!playerRef.current) {
          console.error('Player ref not available');
          return;
        }

        const ytPlayer = new window.YT.Player(playerRef.current, {
          height: '0',
          width: '0',
          videoId: 'ThEftPScNwA',
          playerVars: {
            autoplay: 0,
            controls: 0,
            disablekb: 1,
            enablejsapi: 1,
            fs: 0,
            iv_load_policy: 3,
            modestbranding: 1,
            rel: 0,
            start: 14, // Start at 14 seconds
            end: 0,   // Play until end
            loop: 0,    // We'll handle looping manually
            playlist: 'ThEftPScNwA' // Required for looping
          },
          events: {
            onReady: (event) => {
              console.log('Player ready!');
              try {
                event.target.setVolume(volume * 100);
                setPlayer(event.target);
                setIsReady(true);
                setError(null);
              } catch (err) {
                console.error('Error in onReady:', err);
                setError('เกิดข้อผิดพลาดในการเตรียม player');
              }
            },
            onStateChange: (event) => {
              try {
                const state = event.data;
                const playerState = window.YT.PlayerState;

                if (state === playerState.ENDED) {
                  // Loop back to 14 seconds
                  event.target.seekTo(14);
                  event.target.playVideo();
                } else if (state === playerState.PLAYING) {
                  setIsPlaying(true);
                  // Check if we need to loop (removed time range check since we play full video)
                  console.log('Playing...');
                } else if (state === playerState.PAUSED) {
                  setIsPlaying(false);
                } else if (state === playerState.BUFFERING) {
                  // Handle buffering state
                  console.log('Player is buffering...');
                }
              } catch (err) {
                console.error('Error in onStateChange:', err);
              }
            },
            onError: (event) => {
              console.error('YouTube player error:', event.data);
              const errorMessages = {
                2: 'วิดีโอ ID ไม่ถูกต้อง',
                5: 'เกิดข้อผิดพลาดของ HTML5 player',
                100: 'ไม่พบวิดีโอ',
                101: 'เจ้าของวิดีโอไม่อนุญาตให้ฝังในเว็บไซต์อื่น',
                150: 'เจ้าของวิดีโอไม่อนุญาตให้ฝังในเว็บไซต์อื่น'
              };
              setError(errorMessages[event.data] || `เกิดข้อผิดพลาด: ${event.data}`);
              setIsReady(false);
            }
          }
        });
      } catch (error) {
        console.error('Failed to initialize YouTube player:', error);
        setError('ไม่สามารถเริ่มต้น YouTube player ได้');
      }
    };

    // Add a small delay to ensure DOM is ready
    setTimeout(initializePlayer, 100);

    return () => {
      if (player && typeof player.destroy === 'function') {
        try {
          player.destroy();
        } catch (err) {
          console.error('Error destroying player:', err);
        }
      }
    };
  }, []); // Remove volume dependency to prevent re-initialization

  useEffect(() => {
    if (player && isReady && typeof player.setVolume === 'function') {
      try {
        player.setVolume(volume * 100);
      } catch (err) {
        console.error('Error setting volume:', err);
      }
    }
  }, [volume, player, isReady]);

  const togglePlay = () => {
    if (!player || !isReady) {
      console.log('Player not ready yet');
      return;
    }

    try {
      if (isPlaying) {
        player.pauseVideo();
      } else {
        // Start from 14 seconds
        player.seekTo(14);
        player.playVideo();
      }
    } catch (error) {
      console.error('Error toggling play:', error);
      setError('เกิดข้อผิดพลาดในการควบคุมการเล่น');
    }
  };

  const handleVolumeChange = (e) => {
    const newVolume = parseFloat(e.target.value);
    setVolume(newVolume);
  };

  const clearError = () => {
    setError(null);
  };

  return (
    <div className="background-music" style={{
      position: 'fixed',
      top: '20px',
      right: '20px',
      zIndex: 1000,
      background: 'linear-gradient(135deg, rgba(255, 182, 193, 0.95), rgba(255, 105, 180, 0.95), rgba(255, 20, 147, 0.95))',
      borderRadius: '25px',
      padding: '20px',
      backdropFilter: 'blur(15px)',
      border: '2px solid rgba(255, 255, 255, 0.3)',
      fontFamily: '"Segoe UI", "Arial", "Helvetica", sans-serif',
      maxWidth: '380px',
      boxShadow: '0 8px 32px rgba(255, 20, 147, 0.3), 0 4px 16px rgba(255, 182, 193, 0.2)',
      animation: 'float 3s ease-in-out infinite'
    }}>
      {/* Hidden YouTube player */}
      <div 
        ref={playerRef}
        style={{ display: 'none' }}
      />

      {/* Error display */}
      {error && (
        <div style={{
          background: 'linear-gradient(135deg, rgba(255, 0, 0, 0.2), rgba(255, 100, 100, 0.2))',
          border: '2px solid rgba(255, 100, 100, 0.6)',
          borderRadius: '15px',
          padding: '12px',
          marginBottom: '15px',
          fontSize: '13px',
          color: '#fff',
          display: 'flex',
          justifyContent: 'space-between',
          alignItems: 'center',
          backdropFilter: 'blur(10px)',
          boxShadow: '0 4px 15px rgba(255, 0, 0, 0.2)'
        }}>
          <span style={{ fontWeight: '600' }}>⚠️ {error}</span>
          <button 
            onClick={clearError}
            style={{
              background: 'rgba(255, 255, 255, 0.2)',
              border: '1px solid rgba(255, 255, 255, 0.3)',
              color: '#fff',
              cursor: 'pointer',
              fontSize: '16px',
              padding: '4px 8px',
              borderRadius: '50%',
              width: '28px',
              height: '28px',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
              transition: 'all 0.3s ease',
              fontWeight: 'bold'
            }}
            onMouseEnter={(e) => {
              e.target.style.background = 'rgba(255, 255, 255, 0.3)';
              e.target.style.transform = 'scale(1.1)';
            }}
            onMouseLeave={(e) => {
              e.target.style.background = 'rgba(255, 255, 255, 0.2)';
              e.target.style.transform = 'scale(1)';
            }}
          >
            ✕
          </button>
        </div>
      )}

      {/* Music controls */}
      <div 
        style={{
          display: 'flex',
          alignItems: 'center',
          gap: '18px',
          color: 'white'
        }}
      >
        <button
          onClick={togglePlay}
          disabled={!isReady}
          style={{
            background: isPlaying 
              ? 'linear-gradient(135deg, #ff6b9d, #ff1493, #c71585)' 
              : 'linear-gradient(135deg, #4CAF50, #45a049, #2E7D32)',
            border: '3px solid rgba(255, 255, 255, 0.4)',
            borderRadius: '50%',
            width: '60px',
            height: '60px',
            fontSize: '24px',
            cursor: isReady ? 'pointer' : 'not-allowed',
            transition: 'all 0.4s cubic-bezier(0.175, 0.885, 0.32, 1.275)',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            opacity: isReady ? 1 : 0.6,
            boxShadow: isReady 
              ? '0 6px 20px rgba(255, 20, 147, 0.4), 0 4px 15px rgba(0,0,0,0.2)' 
              : 'none',
            transform: isReady ? 'scale(1)' : 'scale(0.9)',
            filter: isReady ? 'drop-shadow(0 4px 8px rgba(0,0,0,0.3))' : 'none'
          }}
          onMouseEnter={(e) => {
            if (isReady) {
              e.target.style.transform = 'scale(1.15) rotate(5deg)';
              e.target.style.boxShadow = '0 8px 25px rgba(255, 20, 147, 0.6), 0 6px 20px rgba(0,0,0,0.3)';
            }
          }}
          onMouseLeave={(e) => {
            if (isReady) {
              e.target.style.transform = 'scale(1) rotate(0deg)';
              e.target.style.boxShadow = '0 6px 20px rgba(255, 20, 147, 0.4), 0 4px 15px rgba(0,0,0,0.2)';
            }
          }}
        >
          {isPlaying ? '⏸️' : '▶️'}
        </button>

        <div style={{ 
          display: 'flex', 
          alignItems: 'center', 
          gap: '12px', 
          flex: 1,
          background: 'rgba(255, 255, 255, 0.15)',
          padding: '12px 16px',
          borderRadius: '20px',
          border: '1px solid rgba(255, 255, 255, 0.2)',
          backdropFilter: 'blur(10px)'
        }}>
          <span style={{ 
            fontSize: '20px', 
            filter: 'drop-shadow(0 2px 4px rgba(0,0,0,0.3))',
            animation: isPlaying ? 'pulse 1s ease-in-out infinite' : 'none'
          }}>
            🔊
          </span>
          <input
            type="range"
            min="0"
            max="1"
            step="0.1"
            value={volume}
            onChange={handleVolumeChange}
            disabled={!isReady}
            style={{
              width: '90px',
              height: '6px',
              borderRadius: '10px',
              background: isReady 
                ? 'linear-gradient(90deg, #ff69b4, #ff1493)' 
                : 'rgba(255, 255, 255, 0.3)',
              outline: 'none',
              cursor: isReady ? 'pointer' : 'not-allowed',
              opacity: isReady ? 1 : 0.5,
              WebkitAppearance: 'none',
              appearance: 'none'
            }}
            onInput={(e) => {
              const value = e.target.value;
              e.target.style.background = `linear-gradient(90deg, #ff69b4 ${value * 100}%, rgba(255, 255, 255, 0.3) ${value * 100}%)`;
            }}
          />
          <span style={{ 
            fontSize: '14px', 
            minWidth: '35px', 
            fontWeight: '600',
            textShadow: '0 1px 2px rgba(0,0,0,0.3)'
          }}>
            {Math.round(volume * 100)}%
          </span>
        </div>
      </div>

      {/* Song info */}
      <div style={{
        marginTop: '15px',
        textAlign: 'center',
        color: 'white',
        background: 'rgba(255, 255, 255, 0.1)',
        padding: '12px 16px',
        borderRadius: '18px',
        border: '1px solid rgba(255, 255, 255, 0.2)',
        backdropFilter: 'blur(8px)'
      }}>
        <div style={{ 
          fontWeight: 'bold', 
          marginBottom: '4px', 
          fontSize: '16px',
          textShadow: '0 2px 4px rgba(0,0,0,0.3)',
          letterSpacing: '0.5px'
        }}>
          🎵 FALL IN LOVE W/NOT 🎵
        </div>
        <div style={{ 
          opacity: 0.9, 
          fontSize: '12px',
          fontWeight: '500',
          textShadow: '0 1px 2px rgba(0,0,0,0.3)'
        }}>
          {error ? '❌ เกิดข้อผิดพลาด' : 
           isReady ? (isPlaying ? '🎶 กำลังเล่นเพลงรัก...' : '💕 พร้อมเล่นเพลงรัก') : '⏳ กำลังโหลดเพลงรัก...'}
        </div>
      </div>

      {/* Decorative elements */}
      <div style={{
        position: 'absolute',
        top: '-8px',
        left: '-8px',
        right: '-8px',
        bottom: '-8px',
        background: 'linear-gradient(45deg, transparent 30%, rgba(255, 255, 255, 0.1) 50%, transparent 70%)',
        borderRadius: '25px',
        zIndex: -1,
        animation: 'shimmer 3s ease-in-out infinite'
      }} />
    </div>
  );
};

export default BackgroundMusic;
