import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion';
import './LoveMessage.css';

const LoveMessage = ({ message, onClose }) => {
  const [randomPosition, setRandomPosition] = useState({ x: 0, y: 0 });
  
  useEffect(() => {
    // ‡∏™‡∏£‡πâ‡∏≤‡∏á‡∏ï‡∏≥‡πÅ‡∏´‡∏ô‡πà‡∏á random ‡∏ó‡∏µ‡πà‡πÑ‡∏°‡πà‡πÑ‡∏Å‡∏•‡πÄ‡∏Å‡∏¥‡∏ô‡πÑ‡∏õ‡∏à‡∏≤‡∏Å‡∏Å‡∏•‡∏≤‡∏á‡∏´‡∏ô‡πâ‡∏≤‡∏à‡∏≠
    const maxOffset = 200; // ‡∏£‡∏∞‡∏¢‡∏∞‡∏ó‡∏≤‡∏á‡∏™‡∏π‡∏á‡∏™‡∏∏‡∏î‡∏à‡∏≤‡∏Å‡∏Å‡∏•‡∏≤‡∏á‡∏´‡∏ô‡πâ‡∏≤‡∏à‡∏≠
    const x = (Math.random() - 0.5) * maxOffset * 2; // -200 ‡∏ñ‡∏∂‡∏á 200
    const y = (Math.random() - 0.5) * maxOffset * 2; // -200 ‡∏ñ‡∏∂‡∏á 200
    setRandomPosition({ x, y });
    console.log('Random position:', { x, y });
  }, []);

  console.log('LoveMessage rendered with message:', message);
  
  return (
    <motion.div
      className="love-message-popup"
      initial={{ opacity: 0, scale: 0 }}
      animate={{ 
        opacity: 1, 
        scale: 1
      }}
      exit={{ opacity: 0, scale: 0 }}
      transition={{ 
        type: "spring", 
        stiffness: 300, 
        damping: 20,
        duration: 0.5
      }}
      style={{
        position: 'fixed',
        top: '50%',
        left: '50%',
        transform: `translate(calc(-50% + ${randomPosition.x}px), calc(-50% + ${randomPosition.y}px))`
      }}
    >
      <motion.div
        className="message-heart"
        animate={{
          scale: [1, 1.2, 1],
          rotate: [0, 10, -10, 0]
        }}
        transition={{
          duration: 1.5,
          repeat: Infinity,
          ease: "easeInOut"
        }}
      >
        üíñ
      </motion.div>

      <motion.p
        className="message-text"
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ delay: 0.3, duration: 0.5 }}
      >
        {message}
      </motion.p>

      <motion.button
        className="close-button"
        onClick={onClose}
        whileHover={{ scale: 1.1 }}
        whileTap={{ scale: 0.9 }}
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ delay: 0.5, duration: 0.5 }}
      >
        ‡∏õ‡∏¥‡∏î
      </motion.button>

      {/* Floating hearts around the message */}
      {[...Array(6)].map((_, i) => (
        <motion.div
          key={i}
          className="message-floating-heart"
          initial={{ 
            opacity: 0,
            scale: 0,
            x: 0,
            y: 0
          }}
          animate={{ 
            opacity: [0, 1, 0],
            scale: [0, 1, 0],
            x: Math.cos(i * 60 * Math.PI / 180) * 80,
            y: Math.sin(i * 60 * Math.PI / 180) * 80
          }}
          transition={{
            duration: 2,
            repeat: Infinity,
            delay: i * 0.2,
            ease: "easeInOut"
          }}
        >
          {['üíñ', 'üíï', 'üíó', 'üíì', 'üíò', 'üíû'][i % 6]}
        </motion.div>
      ))}
    </motion.div>
  );
};

export default LoveMessage;
