import React, { useState, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import './App.css';
import HeartbeatMonitor from './components/HeartbeatMonitor';
import LoveButton from './components/LoveButton';
import LoveMessage from './components/LoveMessage';
import BackgroundMusic from './components/BackgroundMusic';

function App() {
  const [showMessage, setShowMessage] = useState(false);
  const [currentMessage, setCurrentMessage] = useState(0);
  const [isNoveltyMode, setIsNoveltyMode] = useState(true);

  const loveMessages = [
    "‡πÄ‡∏Ñ‡πâ‡∏≤‡∏£‡∏±‡∏Å‡πÄ‡∏ö‡∏ö‡∏µ‡πã ‡∏≠‡∏¥‡∏≠‡∏¥",
    "‡πÄ‡∏Ñ‡πâ‡∏≤‡∏£‡∏±‡∏Å‡πÄ‡∏ö‡∏ö‡∏µ‡πã‡∏Ñ‡∏ô‡πÄ‡∏î‡∏µ‡∏¢‡∏ß",
    "Happy birthday to the one Who stole my heart !",
    "üíó ‡∏ó‡∏∏‡∏Å‡∏à‡∏±‡∏á‡∏´‡∏ß‡∏∞‡∏Å‡∏≤‡∏£‡πÄ‡∏ï‡πâ‡∏ô‡∏Ç‡∏≠‡∏á‡∏´‡∏±‡∏ß‡πÉ‡∏à‡πÄ‡∏õ‡πá‡∏ô‡∏Ç‡∏≠‡∏á‡πÄ‡∏ò‡∏≠ üíó",
    "üíì ‡πÄ‡∏ò‡∏≠‡∏Ñ‡∏∑‡∏≠‡∏à‡∏±‡∏á‡∏´‡∏ß‡∏∞‡∏Ç‡∏≠‡∏á‡∏´‡∏±‡∏ß‡πÉ‡∏à‡πÄ‡∏Ñ‡πâ‡∏≤ üíì",
    "üíò ‡∏Ñ‡∏ß‡∏≤‡∏°‡∏£‡∏±‡∏Å‡∏Ç‡∏≠‡∏á‡πÄ‡∏Ñ‡πâ‡∏≤‡∏ï‡πà‡∏≠‡πÄ‡∏ò‡∏≠‡πÅ‡∏Ç‡πá‡∏á‡πÅ‡∏Å‡∏£‡πà‡∏á‡∏Ç‡∏∂‡πâ‡∏ô‡∏ó‡∏∏‡∏Å‡∏à‡∏±‡∏á‡∏´‡∏ß‡∏∞‡∏Å‡∏≤‡∏£‡πÄ‡∏ï‡πâ‡∏ô üíò",
    "üíû ‡πÄ‡∏ò‡∏≠‡∏Ñ‡∏∑‡∏≠‡∏Ñ‡∏π‡πà‡πÅ‡∏ó‡πâ‡∏Ç‡∏≠‡∏á‡πÄ‡∏Ñ‡πâ‡∏≤ ‡∏à‡∏¥‡∏ï‡∏ß‡∏¥‡∏ç‡∏ç‡∏≤‡∏ì‡∏ó‡∏µ‡πà‡∏Ñ‡∏π‡πà‡∏Å‡∏±‡∏ô üíû",
    "üíü ‡πÄ‡∏Ñ‡πâ‡∏≤‡πÇ‡∏ä‡∏Ñ‡∏î‡∏µ‡∏°‡∏≤‡∏Å‡∏ó‡∏µ‡πà‡∏°‡∏µ‡πÄ‡∏ò‡∏≠‡πÉ‡∏ô‡∏ä‡∏µ‡∏ß‡∏¥‡∏ï üíü"
  ];

  const handleLoveClick = () => {
    setCurrentMessage(Math.floor(Math.random() * loveMessages.length));
    setShowMessage(true);
  };

  const handleMessageClose = () => {
    setShowMessage(false);
  };

  return (
    <div className="App">
      {/* Background Music */}
      <BackgroundMusic />
      
      {/* Background Grid */}
      <div className="grid"></div>
      
      {/* Main Content */}
      <div className="content">
        <motion.h1 
          className="title"
          initial={{ opacity: 0, y: -50 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 1, delay: 0.5 }}
        >
          To the sweetest boyfriend ever
        </motion.h1>
        
        <motion.p 
          className="subtitle"
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ duration: 1, delay: 1 }}
        >
          ·ÅÑ·ÅÉ HAPPY BIRTHDAY MY LOVE ·ÅÑ·ÅÉ
        </motion.p>

        {/* Heartbeat Monitor */}
        <HeartbeatMonitor isNoveltyMode={isNoveltyMode} />

        {/* Mode Toggle */}
        <motion.div 
          className="mode-toggle"
          initial={{ opacity: 0, scale: 0.8 }}
          animate={{ opacity: 1, scale: 1 }}
          transition={{ duration: 0.5, delay: 1.5 }}
        >
          <label className="toggle-label">
            <input 
              type="checkbox" 
              checked={isNoveltyMode}
              onChange={(e) => setIsNoveltyMode(e.target.checked)}
            />
            <span className="toggle-text">
              {isNoveltyMode ? "üíó ‡πÇ‡∏´‡∏°‡∏î‡∏û‡∏¥‡πÄ‡∏®‡∏©" : "üíô ‡πÇ‡∏´‡∏°‡∏î‡∏Ñ‡∏•‡∏≤‡∏™‡∏™‡∏¥‡∏Å"}
            </span>
          </label>
        </motion.div>

        {/* Love Button */}
        <LoveButton onClick={handleLoveClick} />

        {/* Love Messages */}
        <AnimatePresence>
          {showMessage && (
            <LoveMessage 
              message={loveMessages[currentMessage]}
              onClose={handleMessageClose}
            />
          )}
        </AnimatePresence>
      </div>

      {/* Floating Hearts */}
      <div className="floating-hearts">
        {[...Array(20)].map((_, i) => (
          <motion.div
            key={i}
            className="floating-heart"
            initial={{ 
              x: Math.random() * window.innerWidth,
              y: window.innerHeight + 100,
              opacity: 0
            }}
            animate={{ 
              y: -100,
              opacity: [0, 1, 0],
              scale: [0, 1, 0]
            }}
            transition={{
              duration: Math.random() * 3 + 2,
              repeat: Infinity,
              delay: Math.random() * 2,
              ease: "easeInOut"
            }}
          >
            {['üíñ', 'üíï', 'üíó', 'üíì', 'üíò', 'üíû', 'üíü'][Math.floor(Math.random() * 7)]}
          </motion.div>
        ))}
      </div>
    </div>
  );
}

export default App;
