import React, { useState, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import './App.css';
import HeartbeatMonitor from './components/HeartbeatMonitor';
import LoveButton from './components/LoveButton';
import LoveMessage from './components/LoveMessage';
import BackgroundMusic from './components/BackgroundMusic';
import LoveLock from './components/LoveLock';

function App() {
  const [isUnlocked, setIsUnlocked] = useState(false);
  const [showMessage, setShowMessage] = useState(false);
  const [currentMessage, setCurrentMessage] = useState(0);

  const loveMessages = [
    "‡πÄ‡∏Ñ‡πâ‡∏≤‡∏£‡∏±‡∏Å‡πÄ‡∏ö‡∏ö‡∏µ‡πã ‡∏≠‡∏¥‡∏≠‡∏¥",
    "‡πÄ‡∏Ñ‡πâ‡∏≤‡∏£‡∏±‡∏Å‡πÄ‡∏ö‡∏ö‡∏µ‡πã‡∏ó‡∏µ‡πà‡∏™‡∏∏‡∏î‡πÉ‡∏ô‡πÇ‡∏•‡∏Å(‡∏Ç‡∏≠‡∏á‡πÄ‡∏Ñ‡πâ‡∏≤‡πÄ‡∏≠‡∏á)",
    "Happy birthday to the one Who stole my heart !",
    "‡∏Ç‡∏≠‡πÉ‡∏´‡πâ‡πÄ‡∏ö‡∏ö‡∏µ‡πã‡πÑ‡∏°‡πà‡∏õ‡∏ß‡∏î‡∏´‡∏•‡∏±‡∏á‡∏ï‡∏•‡∏≠‡∏î‡πÑ‡∏õ",
    "‡∏Ñ‡∏¥‡∏î‡πÑ‡∏°‡πà‡∏≠‡∏≠‡∏Å‡∏•‡∏∞ ‡∏Ñ‡∏¥‡∏î‡∏ñ‡∏∂‡∏á‡πÅ‡∏ï‡πà‡πÄ‡∏ö‡∏ö‡∏µ‡πã",
    
  ];

  const handleLoveClick = () => {
    setCurrentMessage(Math.floor(Math.random() * loveMessages.length));
    setShowMessage(true);
  };

  const handleMessageClose = () => {
    setShowMessage(false);
  };

  return (
    <div className="App">
      {/* Background Music */}
      <BackgroundMusic />
      
      {!isUnlocked && (
        <LoveLock onUnlock={() => setIsUnlocked(true)} />
      )}
      
      {isUnlocked && (
        <>
      {/* Background Grid */}
      <div className="grid"></div>
      
      {/* Main Content */}
      <div className="content">
        <motion.h1 
          className="title"
          initial={{ opacity: 0, y: -50 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 1, delay: 0.5 }}
        >
          ‚ùÄ To the sweetest boyfriend ever ‚ùÄ
        </motion.h1>
        
        <motion.p 
          className="subtitle"
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ duration: 1, delay: 1 }}
        >
sending you birthday wishes, xoxo
</motion.p>

        {/* Heartbeat Monitor */}
        <HeartbeatMonitor />

        {/* Love Button */}
        <LoveButton onClick={handleLoveClick} />

        {/* Love Messages */}
        <AnimatePresence>
          {showMessage && (
            <LoveMessage 
              message={loveMessages[currentMessage]}
              onClose={handleMessageClose}
            />
          )}
        </AnimatePresence>
      </div>

      {/* Floating Hearts */}
      <div className="floating-hearts">
        {[...Array(20)].map((_, i) => (
          <motion.div
            key={i}
            className="floating-heart"
            initial={{ 
              x: Math.random() * window.innerWidth,
              y: window.innerHeight + 100,
              opacity: 0
            }}
            animate={{ 
              y: -100,
              opacity: [0, 1, 0],
              scale: [0, 1, 0]
            }}
            transition={{
              duration: Math.random() * 6 + 8, // ‡∏ä‡πâ‡∏≤‡∏•‡∏á‡∏à‡∏≤‡∏Å‡πÄ‡∏î‡∏¥‡∏°
              repeat: Infinity,
              delay: Math.random() * 4, // ‡∏´‡∏ô‡πà‡∏ß‡∏á‡πÄ‡∏ß‡∏•‡∏≤‡∏°‡∏≤‡∏Å‡∏Ç‡∏∂‡πâ‡∏ô
              ease: "easeInOut"
            }}
          >
            {[' ö…û', '‚ô°', '‚úø', '‚ù§Ô∏é', '‚úÆ', 'îìò', '‚ú¶Ô∏é'][Math.floor(Math.random() * 7)]}
          </motion.div>
        ))}
      </div>
        </>
      )}
    </div>
  );
}

export default App;
