import React, { useState, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import './App.css';
import HeartbeatMonitor from './components/HeartbeatMonitor';
import LoveButton from './components/LoveButton';
import LoveMessage from './components/LoveMessage';

function App() {
  const [showMessage, setShowMessage] = useState(false);
  const [currentMessage, setCurrentMessage] = useState(0);
  const [isNoveltyMode, setIsNoveltyMode] = useState(true);

  const loveMessages = [
    "💖 I love you more than words can express! 💖",
    "💕 You make my heart skip a beat every day 💕",
    "💝 You are my everything, my love 💝",
    "💗 Every heartbeat belongs to you 💗",
    "💓 You are the rhythm of my heart 💓",
    "💘 My love for you grows stronger with every beat 💘",
    "💞 You are my perfect match, my soulmate 💞",
    "💟 I'm so lucky to have you in my life 💟"
  ];

  const handleLoveClick = () => {
    setCurrentMessage(Math.floor(Math.random() * loveMessages.length));
    setShowMessage(true);
  };

  const handleMessageClose = () => {
    setShowMessage(false);
  };

  return (
    <div className="App">
      {/* Background Grid */}
      <div className="grid"></div>
      
      {/* Main Content */}
      <div className="content">
        <motion.h1 
          className="title"
          initial={{ opacity: 0, y: -50 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 1, delay: 0.5 }}
        >
          💖 Heartbeat Monitor 💖
        </motion.h1>
        
        <motion.p 
          className="subtitle"
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ duration: 1, delay: 1 }}
        >
          For the one who makes my heart race... 💕
        </motion.p>

        {/* Heartbeat Monitor */}
        <HeartbeatMonitor isNoveltyMode={isNoveltyMode} />

        {/* Mode Toggle */}
        <motion.div 
          className="mode-toggle"
          initial={{ opacity: 0, scale: 0.8 }}
          animate={{ opacity: 1, scale: 1 }}
          transition={{ duration: 0.5, delay: 1.5 }}
        >
          <label className="toggle-label">
            <input 
              type="checkbox" 
              checked={isNoveltyMode}
              onChange={(e) => setIsNoveltyMode(e.target.checked)}
            />
            <span className="toggle-text">
              {isNoveltyMode ? "💚 Novelty Mode" : "💙 Classic Mode"}
            </span>
          </label>
        </motion.div>

        {/* Love Button */}
        <LoveButton onClick={handleLoveClick} />

        {/* Love Messages */}
        <AnimatePresence>
          {showMessage && (
            <LoveMessage 
              message={loveMessages[currentMessage]}
              onClose={handleMessageClose}
            />
          )}
        </AnimatePresence>
      </div>

      {/* Floating Hearts */}
      <div className="floating-hearts">
        {[...Array(20)].map((_, i) => (
          <motion.div
            key={i}
            className="floating-heart"
            initial={{ 
              x: Math.random() * window.innerWidth,
              y: window.innerHeight + 100,
              opacity: 0
            }}
            animate={{ 
              y: -100,
              opacity: [0, 1, 0],
              scale: [0, 1, 0]
            }}
            transition={{
              duration: Math.random() * 3 + 2,
              repeat: Infinity,
              delay: Math.random() * 2,
              ease: "easeInOut"
            }}
          >
            {['💖', '💕', '💗', '💓', '💘', '💞', '💟'][Math.floor(Math.random() * 7)]}
          </motion.div>
        ))}
      </div>
    </div>
  );
}

export default App;
