import React, { useState, useEffect, useRef } from 'react';

const BackgroundMusic = () => {
  const [isPlaying, setIsPlaying] = useState(false);
  const [volume, setVolume] = useState(0.5);
  const [player, setPlayer] = useState(null);
  const [isReady, setIsReady] = useState(false);
  const playerRef = useRef(null);

  useEffect(() => {
    // Load YouTube API
    const loadYouTubeAPI = () => {
      return new Promise((resolve) => {
        if (window.YT && window.YT.Player) {
          resolve();
          return;
        }

        window.onYouTubeIframeAPIReady = resolve;
        
        if (!document.querySelector('script[src*="youtube"]')) {
          const script = document.createElement('script');
          script.src = 'https://www.youtube.com/iframe_api';
          script.async = true;
          document.head.appendChild(script);
        }
      });
    };

    const initializePlayer = async () => {
      try {
        await loadYouTubeAPI();
        
        const ytPlayer = new window.YT.Player(playerRef.current, {
          height: '0',
          width: '0',
          videoId: '1QkX7Qetu2Q',
          playerVars: {
            autoplay: 0,
            controls: 0,
            disablekb: 1,
            enablejsapi: 1,
            fs: 0,
            iv_load_policy: 3,
            modestbranding: 1,
            rel: 0,
            start: 175, // Start at 2:55 (175 seconds)
            end: 230,   // End at 3:50 (230 seconds)
          },
          events: {
            onReady: (event) => {
              console.log('Player ready!');
              event.target.setVolume(volume * 100);
              setPlayer(event.target);
              setIsReady(true);
            },
            onStateChange: (event) => {
              if (event.data === window.YT.PlayerState.ENDED) {
                // Loop the specific section
                event.target.seekTo(175);
                event.target.playVideo();
              } else if (event.data === window.YT.PlayerState.PLAYING) {
                setIsPlaying(true);
              } else if (event.data === window.YT.PlayerState.PAUSED) {
                setIsPlaying(false);
              }
            },
            onError: (event) => {
              console.error('YouTube player error:', event.data);
            }
          }
        });
      } catch (error) {
        console.error('Failed to initialize YouTube player:', error);
      }
    };

    initializePlayer();

    return () => {
      if (player) {
        player.destroy();
      }
    };
  }, []);

  useEffect(() => {
    if (player && isReady) {
      player.setVolume(volume * 100);
    }
  }, [volume, player, isReady]);

  const togglePlay = () => {
    if (!player || !isReady) {
      console.log('Player not ready yet');
      return;
    }

    try {
      if (isPlaying) {
        player.pauseVideo();
      } else {
        // Ensure we're in the correct time range
        player.seekTo(175);
        player.playVideo();
      }
    } catch (error) {
      console.error('Error toggling play:', error);
    }
  };

  const handleVolumeChange = (e) => {
    const newVolume = parseFloat(e.target.value);
    setVolume(newVolume);
  };

  return (
    <div className="background-music" style={{
      position: 'fixed',
      top: '20px',
      right: '20px',
      zIndex: 1000,
      background: 'rgba(0, 0, 0, 0.8)',
      borderRadius: '15px',
      padding: '15px',
      backdropFilter: 'blur(10px)',
      border: '1px solid rgba(255, 255, 255, 0.1)',
      fontFamily: 'Arial, sans-serif'
    }}>
      {/* Hidden YouTube player */}
      <div 
        ref={playerRef}
        style={{ display: 'none' }}
      />
      
      {/* Music controls */}
      <div 
        style={{
          display: 'flex',
          alignItems: 'center',
          gap: '15px',
          color: 'white',
          minWidth: '300px'
        }}
      >
        <button
          onClick={togglePlay}
          disabled={!isReady}
          style={{
            background: isPlaying ? 'linear-gradient(45deg, #ff6b6b, #ee5a24)' : 'linear-gradient(45deg, #666, #444)',
            border: 'none',
            borderRadius: '50%',
            width: '50px',
            height: '50px',
            fontSize: '20px',
            cursor: isReady ? 'pointer' : 'not-allowed',
            transition: 'all 0.3s ease',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            opacity: isReady ? 1 : 0.5
          }}
          onMouseEnter={(e) => {
            if (isReady) e.target.style.transform = 'scale(1.1)';
          }}
          onMouseLeave={(e) => {
            e.target.style.transform = 'scale(1)';
          }}
        >
          {isPlaying ? '⏸️' : '▶️'}
        </button>
        
        <div style={{ display: 'flex', alignItems: 'center', gap: '10px' }}>
          <span style={{ fontSize: '16px' }}>🔊</span>
          <input
            type="range"
            min="0"
            max="1"
            step="0.1"
            value={volume}
            onChange={handleVolumeChange}
            style={{
              width: '80px',
              height: '5px',
              borderRadius: '5px',
              background: '#ddd',
              outline: 'none',
              cursor: 'pointer'
            }}
          />
          <span style={{ fontSize: '12px', minWidth: '30px' }}>
            {Math.round(volume * 100)}%
          </span>
        </div>
        
        <div style={{
          display: 'flex',
          flexDirection: 'column',
          alignItems: 'center',
          fontSize: '12px',
          textAlign: 'center'
        }}>
          <div style={{ fontWeight: 'bold', marginBottom: '2px' }}>
            I LOVE NOT A CHATAHI 𖹭
          </div>
          <div style={{ opacity: 0.7, fontSize: '10px' }}>
            {isReady ? (isPlaying ? 'กำลังเล่น' : 'พร้อมเล่น') : 'กำลังโหลด...'}
          </div>
        </div>
      </div>
    </div>
  );
};

export default BackgroundMusic;
