import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion';
import './LoveMessage.css';

const LoveMessage = ({ message, onClose }) => {
  const [randomPosition, setRandomPosition] = useState({ x: 0, y: 0 });
  
  useEffect(() => {
    // ‡∏™‡∏£‡πâ‡∏≤‡∏á‡∏ï‡∏≥‡πÅ‡∏´‡∏ô‡πà‡∏á random ‡∏ó‡∏µ‡πà‡∏´‡∏•‡∏≤‡∏Å‡∏´‡∏•‡∏≤‡∏¢‡∏°‡∏≤‡∏Å‡∏Ç‡∏∂‡πâ‡∏ô
    const generateRandomPosition = () => {
      // ‡∏™‡∏∏‡πà‡∏°‡∏ï‡∏≥‡πÅ‡∏´‡∏ô‡πà‡∏á‡πÅ‡∏ö‡∏ö‡∏Å‡∏£‡∏∞‡∏à‡∏≤‡∏¢‡∏ó‡∏±‡πà‡∏ß‡∏´‡∏ô‡πâ‡∏≤‡∏à‡∏≠
      const screenWidth = window.innerWidth;
      const screenHeight = window.innerHeight;
      
      // ‡∏Ñ‡∏≥‡∏ô‡∏ß‡∏ì‡∏Ç‡∏ô‡∏≤‡∏î‡∏Ç‡∏≠‡∏á popup (‡∏õ‡∏£‡∏∞‡∏°‡∏≤‡∏ì 400px width, 300px height)
      const popupWidth = 400;
      const popupHeight = 300;
      
      // ‡∏™‡∏∏‡πà‡∏°‡∏ï‡∏≥‡πÅ‡∏´‡∏ô‡πà‡∏á x ‡πÅ‡∏•‡∏∞ y ‡πÅ‡∏ö‡∏ö‡∏Å‡∏£‡∏∞‡∏à‡∏≤‡∏¢‡∏ó‡∏±‡πà‡∏ß‡∏´‡∏ô‡πâ‡∏≤‡∏à‡∏≠
      // ‡∏´‡πà‡∏≤‡∏á‡∏Ç‡∏≠‡∏ö‡∏à‡∏≠‡∏≠‡∏¢‡πà‡∏≤‡∏á‡∏ô‡πâ‡∏≠‡∏¢ 50px ‡πÅ‡∏•‡∏∞‡πÑ‡∏°‡πà‡πÉ‡∏´‡πâ popup ‡πÄ‡∏Å‡∏¥‡∏ô‡∏Ç‡∏≠‡∏ö‡∏à‡∏≠
      const minX = 50;
      const maxX = screenWidth - popupWidth - 50;
      const minY = 50;
      const maxY = screenHeight - popupHeight - 50;
      
      const x = Math.random() * (maxX - minX) + minX;
      const y = Math.random() * (maxY - minY) + minY;
      
      return { x: Math.floor(x), y: Math.floor(y) };
    };
    
    const newPosition = generateRandomPosition();
    setRandomPosition(newPosition);
    console.log('New random position:', newPosition);
  }, [message]); // ‡πÄ‡∏õ‡∏•‡∏µ‡πà‡∏¢‡∏ô‡∏ï‡∏≥‡πÅ‡∏´‡∏ô‡πà‡∏á‡∏ó‡∏∏‡∏Å‡∏Ñ‡∏£‡∏±‡πâ‡∏á‡∏ó‡∏µ‡πà message ‡πÄ‡∏õ‡∏•‡∏µ‡πà‡∏¢‡∏ô

  console.log('LoveMessage rendered with message:', message);
  
  return (
    <motion.div
      className="love-message-popup"
      initial={{ opacity: 0, scale: 0, rotate: -180 }}
      animate={{ 
        opacity: 1, 
        scale: 1,
        rotate: 0
      }}
      exit={{ opacity: 0, scale: 0, rotate: 180 }}
      transition={{ 
        type: "spring", 
        stiffness: 200, 
        damping: 15,
        duration: 0.6
      }}
      style={{
        position: 'fixed',
        top: `${randomPosition.y}px`,
        left: `${randomPosition.x}px`,
        transform: 'none', // ‡πÑ‡∏°‡πà‡πÉ‡∏ä‡πâ transform ‡πÅ‡∏ö‡∏ö‡πÄ‡∏î‡∏¥‡∏°
        zIndex: 1000
      }}
    >
      {/* Background glow effect */}
      <motion.div
        className="message-glow"
        animate={{
          opacity: [0.3, 0.6, 0.3],
          scale: [0.95, 1.05, 0.95]
        }}
        transition={{
          duration: 3,
          repeat: Infinity,
          ease: "easeInOut"
        }}
        style={{
          position: 'absolute',
          top: '-20px',
          left: '-20px',
          right: '-20px',
          bottom: '-20px',
          background: 'radial-gradient(circle, rgba(255, 20, 147, 0.2), transparent)',
          borderRadius: '25px',
          zIndex: -1,
          filter: 'blur(15px)'
        }}
      />

      {/* Decorative border */}
      <motion.div
        className="message-border"
        animate={{
          rotate: 360
        }}
        transition={{
          duration: 20,
          repeat: Infinity,
          ease: "linear"
        }}
        style={{
          position: 'absolute',
          top: '-3px',
          left: '-3px',
          right: '-3px',
          bottom: '-3px',
          background: 'linear-gradient(45deg, #ff69b4, #ff1493, #c71585, #ff69b4, #ff69b4)',
          borderRadius: '25px',
          zIndex: -1,
          opacity: 0.8
        }}
      />

      <motion.div
        className="message-heart"
        animate={{
          scale: [1, 1.3, 1],
          rotate: [0, 15, -15, 0],
          y: [0, -10, 0]
        }}
        transition={{
          duration: 2,
          repeat: Infinity,
          ease: "easeInOut"
        }}
        style={{
          fontSize: '3.5rem',
          textAlign: 'center',
          marginBottom: '1rem',
          filter: 'drop-shadow(0 4px 12px rgba(255, 20, 147, 0.6))',
          animation: 'heartFloat 3s ease-in-out infinite'
        }}
      >
        üíñ
      </motion.div>

      <motion.p
        className="message-text"
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ delay: 0.4, duration: 0.6, type: "spring" }}
        style={{
          fontSize: '1.2rem',
          fontWeight: '600',
          color: '#fff',
          textAlign: 'center',
          marginBottom: '1.5rem',
          lineHeight: '1.5',
          textShadow: '0 2px 8px rgba(0,0,0,0.4)',
          fontFamily: '"Poppins", "Segoe UI", sans-serif',
          letterSpacing: '0.5px'
        }}
      >
        {message}
      </motion.p>

      <motion.button
        className="close-button"
        onClick={onClose}
        whileHover={{ 
          scale: 1.15,
          rotate: [0, -5, 5, 0],
          transition: { duration: 0.3, type: "spring" }
        }}
        whileTap={{ scale: 0.85 }}
        initial={{ opacity: 0, scale: 0.8 }}
        animate={{ opacity: 1, scale: 1 }}
        transition={{ delay: 0.6, duration: 0.5, type: "spring" }}
        style={{
          background: 'linear-gradient(135deg, rgba(255, 255, 255, 0.2), rgba(255, 255, 255, 0.1))',
          border: '2px solid rgba(255, 255, 255, 0.4)',
          borderRadius: '25px',
          padding: '0.8rem 2rem',
          color: '#fff',
          fontSize: '1rem',
          fontWeight: '600',
          cursor: 'pointer',
          backdropFilter: 'blur(10px)',
          transition: 'all 0.3s ease',
          textShadow: '0 1px 3px rgba(0,0,0,0.3)',
          boxShadow: '0 4px 15px rgba(0,0,0,0.2)'
        }}
      >
        üíï ‡∏õ‡∏¥‡∏î üíï
      </motion.button>

      {/* Enhanced floating hearts around the message */}
      {[...Array(8)].map((_, i) => (
        <motion.div
          key={i}
          className="message-floating-heart"
          initial={{ 
            opacity: 0,
            scale: 0,
            x: 0,
            y: 0
          }}
          animate={{ 
            opacity: [0, 1, 0],
            scale: [0, 1, 0],
            x: Math.cos(i * 45 * Math.PI / 180) * 100,
            y: Math.sin(i * 45 * Math.PI / 180) * 100
          }}
          transition={{
            duration: 3,
            repeat: Infinity,
            delay: i * 0.3,
            ease: "easeInOut"
          }}
          style={{
            position: 'absolute',
            fontSize: '1.2rem',
            filter: 'drop-shadow(0 0 8px rgba(255, 105, 180, 0.6))',
            zIndex: 1001
          }}
        >
          {['üíñ', 'üíï', 'üíó', 'üíì', 'üíò', 'üíû', 'üíù', 'üíå'][i % 8]}
        </motion.div>
      ))}

      {/* Additional decorative elements */}
      <motion.div
        className="message-sparkles"
        animate={{
          rotate: 360
        }}
        transition={{
          duration: 30,
          repeat: Infinity,
          ease: "linear"
        }}
        style={{
          position: 'absolute',
          top: '-30px',
          left: '-30px',
          right: '-30px',
          bottom: '-30px',
          pointerEvents: 'none',
          zIndex: -1
        }}
      >
        {[...Array(4)].map((_, i) => (
          <motion.div
            key={`sparkle-${i}`}
            style={{
              position: 'absolute',
              fontSize: '1rem',
              color: '#ffd700',
              filter: 'drop-shadow(0 0 5px rgba(255, 215, 0, 0.8))'
            }}
            animate={{
              opacity: [0.3, 1, 0.3],
              scale: [0.8, 1.2, 0.8]
            }}
            transition={{
              duration: 4,
              repeat: Infinity,
              delay: i * 1,
              ease: "easeInOut"
            }}
          >
            {['‚ú®', 'üí´', '‚≠ê', 'üåü'][i]}
          </motion.div>
        ))}
      </motion.div>
    </motion.div>
  );
};

export default LoveMessage;
