import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion';
import './HeartbeatMonitor.css';

const HeartbeatMonitor = () => {
  const [heartRate, setHeartRate] = useState(72);
  const [isAnimating, setIsAnimating] = useState(false);

  useEffect(() => {
    const interval = setInterval(() => {
      setHeartRate(prev => {
        const newRate = prev + Math.floor(Math.random() * 6) - 3;
        return Math.max(60, Math.min(100, newRate));
      });
    }, 2000);

    return () => clearInterval(interval);
  }, []);

  const handleHeartClick = () => {
    setIsAnimating(true);
    setTimeout(() => setIsAnimating(false), 1000);
  };

  return (
    <motion.div 
      className="heartbeat-monitor"
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      transition={{ duration: 0.8, delay: 1.5 }}
    >
      <motion.div 
        className="monitor-container"
        whileHover={{ scale: 1.05 }}
        transition={{ type: "spring", stiffness: 300 }}
      >
        <div className="monitor-header">
          <span className="monitor-title">‚ù§Ô∏è Heartbeat Monitor ‚ù§Ô∏è</span>
        </div>
        
        <div className="monitor-body">
          <motion.div 
            className="heart-display"
            onClick={handleHeartClick}
            animate={isAnimating ? {
              scale: [1, 1.3, 1],
              rotate: [0, 10, -10, 0]
            } : {}}
            transition={{ duration: 0.5 }}
          >
            üíì
          </motion.div>
          
          <div className="heart-rate">
            <span className="rate-number">{heartRate}</span>
            <span className="rate-unit">BPM</span>
          </div>
          
          <div className="status-indicator">
            <span className="status-dot"></span>
            <span className="status-text">Normal</span>
          </div>
        </div>
        
        <div className="monitor-footer">
          <div className="wave-container">
            {[...Array(8)].map((_, i) => (
              <motion.div
                key={i}
                className="wave-bar"
                animate={{
                  height: [20, 40, 20],
                  opacity: [0.5, 1, 0.5]
                }}
                transition={{
                  duration: 1,
                  repeat: Infinity,
                  delay: i * 0.1,
                  ease: "easeInOut"
                }}
              />
            ))}
          </div>
        </div>
      </motion.div>
    </motion.div>
  );
};

export default HeartbeatMonitor;
