import React from 'react';
import { motion } from 'framer-motion';
import './HeartbeatMonitor.css';

const HeartbeatMonitor = ({ isNoveltyMode }) => {
  const originalPath = "m 2.8096902,901.62064 27.8352018,0 29.835201,0 c 9.945067,0 32.20852,-29.26009 43.835197,0 l 15.8352,0 13.39911,23.5426 16.58744,-153.5426 19.3139,188.52018 16.07175,-58.52018 28.48543,0 c 9.15882,-23.20628 26.67189,-23.20628 34.48542,0 l 22.48542,0 26.48543,0";
  
  const noveltyPath = "m 2.8096902,901.62064 15.8352018,0 7.835201,0 c 9.945067,0 26.20852,-29.26009 37.835197,0 l 9.8352,0 15.39911,23.5426 18.58744,-153.5426 17.3139,188.52018 16.07175,-58.52018 22.48543,0 c 9.15882,-23.20628 26.67189,-23.20628 34.48542,0 l 34.48542,0 c -45.2443,-29.26009 -31.19713,-87.95067 5.62136,-47.92825 33.45524,-39.34978 50.19301,15.64125 5.62135,47.92825 l 46.62136,0 6.62136,0";

  return (
    <div className="heartbeat-monitor">
      <motion.div
        className="ekg-container"
        initial={{ scale: 0, opacity: 0 }}
        animate={{ scale: 1, opacity: 1 }}
        transition={{ duration: 0.8, delay: 1.2 }}
      >
        {/* Original ECG - Classic Mode */}
        {!isNoveltyMode && (
          <>
            <svg
              className="ekg ekg--original"
              viewBox="0 0 300 195"
              preserveAspectRatio="xMinYMin"
            >
              <g transform="translate(-0.13703948,-768.38124)">
                <motion.path
                  className="ekg-path"
                  strokeDasharray="750"
                  strokeDashoffset="750"
                  d={originalPath}
                  animate={{
                    strokeDashoffset: [750, 0, 0],
                    opacity: [0, 1, 0]
                  }}
                  transition={{
                    duration: 1,
                    repeat: Infinity,
                    ease: "easeOut"
                  }}
                />
              </g>
            </svg>
            
            <svg
              className="ekg ekg--original"
              viewBox="0 0 300 195"
              preserveAspectRatio="xMinYMin"
            >
              <g transform="translate(-0.13703948,-768.38124)">
                <motion.path
                  className="ekg-path"
                  strokeDasharray="750"
                  strokeDashoffset="750"
                  d={originalPath}
                  animate={{
                    strokeDashoffset: [750, 0, 0],
                    opacity: [0, 1, 0]
                  }}
                  transition={{
                    duration: 1,
                    repeat: Infinity,
                    delay: 0.5,
                    ease: "easeOut"
                  }}
                />
              </g>
            </svg>
          </>
        )}

        {/* Novelty ECG - Novelty Mode */}
        {isNoveltyMode && (
          <>
            <svg
              className="ekg ekg--novelty"
              viewBox="0 0 300 195"
              preserveAspectRatio="xMinYMin"
            >
              <g transform="translate(-0.13703948,-768.38124)">
                <motion.path
                  className="ekg-path novelty"
                  strokeDasharray="1000"
                  strokeDashoffset="1000"
                  d={noveltyPath}
                  animate={{
                    strokeDashoffset: [1000, 0, 0],
                    opacity: [0, 1, 0]
                  }}
                  transition={{
                    duration: 1.2,
                    repeat: Infinity,
                    ease: "easeOut"
                  }}
                />
              </g>
            </svg>
            
            <svg
              className="ekg ekg--novelty"
              viewBox="0 0 300 195"
              preserveAspectRatio="xMinYMin"
            >
              <g transform="translate(-0.13703948,-768.38124)">
                <motion.path
                  className="ekg-path novelty"
                  strokeDasharray="1000"
                  strokeDashoffset="1000"
                  d={noveltyPath}
                  animate={{
                    strokeDashoffset: [1000, 0, 0],
                    opacity: [0, 1, 0]
                  }}
                  transition={{
                    duration: 1.2,
                    repeat: Infinity,
                    delay: 0.6,
                    ease: "easeOut"
                  }}
                />
              </g>
            </svg>
          </>
        )}
      </motion.div>
    </div>
  );
};

export default HeartbeatMonitor;
