import React, { useState, useEffect, useRef } from 'react';

const BackgroundMusic = () => {
  const [isPlaying, setIsPlaying] = useState(false);
  const [volume, setVolume] = useState(0.5);
  const [player, setPlayer] = useState(null);
  const [isReady, setIsReady] = useState(false);
  const [error, setError] = useState(null);
  const [isExpanded, setIsExpanded] = useState(false);
  const [isMinimized, setIsMinimized] = useState(false);
  const [currentTime, setCurrentTime] = useState(0);
  const [duration, setDuration] = useState(0);
  const [isDragging, setIsDragging] = useState(false);
  const [showPlaylist, setShowPlaylist] = useState(false);
  const [currentSongIndex, setCurrentSongIndex] = useState(0);
  const [autoplayAttempted, setAutoplayAttempted] = useState(false);
  const [autoplayEnabled, setAutoplayEnabled] = useState(true);
  const [isMuted, setIsMuted] = useState(false);
  const [hasStarted, setHasStarted] = useState(false);
  
  const playerRef = useRef(null);
  const initializationRef = useRef(false);
  const progressRef = useRef(null);
  const timeUpdateInterval = useRef(null);
  const playerInstanceRef = useRef(null);

  const playlist = [
    {
      id: 'nR-eDSChKqw',
      title: 'FALL IN LOVE W/NOT',
      artist: 'NOT',
      thumbnail: 'https://img.youtube.com/vi/nR-eDSChKqw/maxresdefault.jpg'
    },
    {
      id: 'lYl7WhMvq9g',
      title: 'ข้อดีข้อเดียวคือรักน้อต',
      artist: 'NOT',
      thumbnail: 'https://img.youtube.com/vi/lYl7WhMvq9g/maxresdefault.jpg'
    },
    {
      id: '1QkX7Qetu2Q',
      title: 'I LOVE NOT A CHATHAI',
      artist: 'NOT',
      thumbnail: 'https://img.youtube.com/vi/1QkX7Qetu2Q/maxresdefault.jpg'
    },
    {
      id: '37tdCAli3N0',
      title: '사랑해',
      artist: 'NOT',
      thumbnail: 'https://img.youtube.com/vi/37tdCAli3N0/maxresdefault.jpg'
    },
    {
      id: 'YAB_X4Tdbgs',
      title: 'NOT IS MINE !!!',
      artist: 'NOT',
      thumbnail: 'https://img.youtube.com/vi/YAB_X4Tdbgs/maxresdefault.jpg'
    },
    {
      id: '7vSdwtHm5q0',
      title: 'LOVE NOT 24 HR.',
      artist: 'NOT',
      thumbnail: 'https://img.youtube.com/vi/7vSdwtHm5q0/maxresdefault.jpg'
    }
  ];

  // Load YouTube API
  const loadYouTubeAPI = () => {
    return new Promise((resolve, reject) => {
      if (window.YT && window.YT.Player) {
        resolve();
        return;
      }

      window.onYouTubeIframeAPIReady = () => {
        resolve();
      };
      
      if (!document.querySelector('script[src*="youtube"]')) {
        const script = document.createElement('script');
        script.src = 'https://www.youtube.com/iframe_api';
        script.async = true;
        script.onerror = () => reject(new Error('Failed to load YouTube API'));
        document.head.appendChild(script);
      } else {
        const checkAPI = () => {
          if (window.YT && window.YT.Player) {
            resolve();
          } else {
            setTimeout(checkAPI, 100);
          }
        };
        checkAPI();
      }
    });
  };

  // Create player with improved state handling
  const createPlayer = (videoId, shouldPlay = false) => {
    return new Promise((resolve, reject) => {
      try {
        const ytPlayer = new window.YT.Player(playerRef.current, {
          height: '0',
          width: '0',
          videoId: videoId,
          playerVars: {
            autoplay: shouldPlay ? 1 : 0,
            controls: 0,
            disablekb: 1,
            enablejsapi: 1,
            fs: 0,
            iv_load_policy: 3,
            modestbranding: 1,
            rel: 0,
            start: 14,
            end: 0,
            loop: 0,
            mute: isMuted ? 1 : 0
          },
          events: {
            onReady: (event) => {
              console.log('Player ready for:', videoId);
              event.target.setVolume(isMuted ? 0 : volume * 100);
              setPlayer(event.target);
              setIsReady(true);
              setError(null);
              
              // Start time tracking
              if (timeUpdateInterval.current) {
                clearInterval(timeUpdateInterval.current);
              }
              
              timeUpdateInterval.current = setInterval(() => {
                if (event.target.getCurrentTime && event.target.getDuration) {
                  const currentTime = event.target.getCurrentTime();
                  const duration = event.target.getDuration();
                  setCurrentTime(currentTime);
                  setDuration(duration);
                }
              }, 1000);
              
              // Attempt to play if needed
              if (shouldPlay && autoplayEnabled) {
                setTimeout(() => {
                  try {
                    event.target.seekTo(14);
                    event.target.playVideo();
                    setHasStarted(true);
                  } catch (err) {
                    console.log('Autoplay failed:', err);
                    setError('คลิกปุ่มเล่นเพื่อเริ่มเพลง');
                  }
                }, 500);
              }
              
              resolve(event.target);
            },
            onStateChange: (event) => {
              const state = event.data;
              const playerState = window.YT.PlayerState;
              
              console.log('State changed to:', state);
              
              if (state === playerState.ENDED) {
                console.log('Song ended, moving to next...');
                setIsPlaying(false);
                setTimeout(() => {
                  nextSong();
                }, 1000);
              } else if (state === playerState.PLAYING) {
                setIsPlaying(true);
                setError(null);
                console.log('Now playing...');
              } else if (state === playerState.PAUSED) {
                setIsPlaying(false);
                console.log('Paused');
              } else if (state === playerState.BUFFERING) {
                console.log('Buffering...');
              }
            },
            onError: (event) => {
              console.error('YouTube player error:', event.data);
              setError('เกิดข้อผิดพลาดในการโหลดเพลง');
              setIsReady(false);
              reject(new Error(`YouTube error: ${event.data}`));
            }
          }
        });
        
        playerInstanceRef.current = ytPlayer;
      } catch (err) {
        reject(err);
      }
    });
  };

  // Initialize player
  useEffect(() => {
    if (initializationRef.current) return;
    initializationRef.current = true;

    const initializePlayer = async () => {
      try {
        await loadYouTubeAPI();
        if (playerRef.current) {
          await createPlayer(playlist[currentSongIndex].id, false);
        }
      } catch (error) {
        console.error('Failed to initialize player:', error);
        setError('ไม่สามารถเริ่มต้น YouTube player ได้');
      }
    };

    setTimeout(initializePlayer, 100);

    return () => {
      if (timeUpdateInterval.current) {
        clearInterval(timeUpdateInterval.current);
      }
      if (playerInstanceRef.current && typeof playerInstanceRef.current.destroy === 'function') {
        try {
          playerInstanceRef.current.destroy();
        } catch (err) {
          console.error('Error destroying player:', err);
        }
      }
    };
  }, []);

  // Update volume
  useEffect(() => {
    if (player && isReady) {
      try {
        if (isMuted) {
          player.setVolume(0);
        } else {
          player.setVolume(volume * 100);
        }
      } catch (err) {
        console.error('Error setting volume:', err);
      }
    }
  }, [volume, player, isReady, isMuted]);

  const togglePlay = () => {
    if (!player || !isReady) {
      console.log('Player not ready');
      return;
    }

    try {
      if (isPlaying) {
        player.pauseVideo();
      } else {
        if (!hasStarted) {
          player.seekTo(14);
          setHasStarted(true);
        }
        player.playVideo();
      }
    } catch (error) {
      console.error('Error toggling play:', error);
      setError('เกิดข้อผิดพลาดในการควบคุมการเล่น');
    }
  };

  const handleVolumeChange = (e) => {
    const newVolume = parseFloat(e.target.value);
    setVolume(newVolume);
    if (newVolume > 0 && isMuted) {
      setIsMuted(false);
    }
  };

  const changeSong = async (index) => {
    if (index === currentSongIndex || index < 0 || index >= playlist.length) {
      return;
    }
    
    console.log('Changing to song:', playlist[index].title);
    
    // Clear current interval
    if (timeUpdateInterval.current) {
      clearInterval(timeUpdateInterval.current);
      timeUpdateInterval.current = null;
    }
    
    // Destroy current player
    if (playerInstanceRef.current) {
      try {
        playerInstanceRef.current.destroy();
      } catch (err) {
        console.error('Error destroying player:', err);
      }
    }
    
    // Reset states
    setPlayer(null);
    setIsReady(false);
    setCurrentTime(0);
    setDuration(0);
    setHasStarted(false);
    setCurrentSongIndex(index);
    
    try {
      // Create new player
      if (playerRef.current) {
        await createPlayer(playlist[index].id, isPlaying);
      }
    } catch (err) {
      console.error('Error changing song:', err);
      setError('เกิดข้อผิดพลาดในการเปลี่ยนเพลง');
    }
  };

  const nextSong = () => {
    const nextIndex = (currentSongIndex + 1) % playlist.length;
    changeSong(nextIndex);
  };

  const prevSong = () => {
    const prevIndex = currentSongIndex === 0 ? playlist.length - 1 : currentSongIndex - 1;
    changeSong(prevIndex);
  };

  const toggleMute = () => {
    if (!player || !isReady) return;
    
    try {
      if (isMuted) {
        setIsMuted(false);
        player.setVolume(volume * 100);
      } else {
        setIsMuted(true);
        player.setVolume(0);
      }
    } catch (err) {
      console.error('Error toggling mute:', err);
    }
  };

  const toggleAutoplay = () => {
    setAutoplayEnabled(!autoplayEnabled);
  };

  const clearError = () => {
    setError(null);
  };

  const toggleExpanded = () => {
    setIsExpanded(!isExpanded);
  };

  const toggleMinimized = () => {
    setIsMinimized(!isMinimized);
  };

  const togglePlaylist = () => {
    setShowPlaylist(!showPlaylist);
  };

  const formatTime = (seconds) => {
    const mins = Math.floor(seconds / 60);
    const secs = Math.floor(seconds % 60);
    return `${mins}:${secs.toString().padStart(2, '0')}`;
  };

  const handleProgressClick = (e) => {
    if (!player || !isReady) return;
    
    const rect = progressRef.current.getBoundingClientRect();
    const clickX = e.clientX - rect.left;
    const progressWidth = rect.width;
    const clickPercent = clickX / progressWidth;
    const newTime = Math.max(14, clickPercent * duration); // Ensure minimum 14 seconds
    
    player.seekTo(newTime);
    setCurrentTime(newTime);
  };

  return (
    <div className={`background-music ${isMinimized ? 'minimized' : ''}`} style={styles.container}>
      {/* Hidden YouTube player */}
      <div ref={playerRef} style={{ display: 'none' }} />
      
      {/* Main Music Player */}
      <div 
        className={`music-player ${isExpanded ? 'expanded' : ''}`}
        onClick={isMinimized ? toggleMinimized : undefined}
        style={{
          ...styles.player,
          ...(isMinimized ? styles.minimized : {}),
          cursor: isMinimized ? 'pointer' : 'default'
        }}
      >
        {/* Header */}
        <div 
          style={styles.header} 
          onClick={!isMinimized ? toggleExpanded : undefined}
        >
          <div style={styles.musicIcon}>
            <span style={styles.musicNote}>♪</span>
            {isPlaying && !isMinimized && (
              <div style={styles.musicWaves}>
                <span style={styles.wave}></span>
                <span style={styles.wave}></span>
                <span style={styles.wave}></span>
              </div>
            )}
          </div>
          
          {!isMinimized && (
            <div style={styles.headerControls}>
              <button 
                style={{...styles.controlBtn, ...(isMuted ? styles.activeBtn : {})}}
                onClick={(e) => {
                  e.stopPropagation();
                  toggleMute();
                }}
                title={isMuted ? "เปิดเสียง" : "ปิดเสียง"}
              >
                {isMuted ? '🔇' : '🔊'}
              </button>
              
              <button 
                style={{...styles.controlBtn, ...(autoplayEnabled ? styles.activeBtn : {})}}
                onClick={(e) => {
                  e.stopPropagation();
                  toggleAutoplay();
                }}
                title={autoplayEnabled ? "ปิด Autoplay" : "เปิด Autoplay"}
              >
                {autoplayEnabled ? '▶️' : '⏸️'}
              </button>
              
              <button 
                style={styles.controlBtn}
                onClick={(e) => {
                  e.stopPropagation();
                  togglePlaylist();
                }}
                title="Playlist"
              >
                📋
              </button>
              
              <button 
                style={styles.controlBtn}
                onClick={(e) => {
                  e.stopPropagation();
                  toggleMinimized();
                }}
                title={isMinimized ? "ขยาย" : "ย่อ"}
              >
                {isMinimized ? '🔽' : '🔼'}
              </button>
              
              <div style={styles.expandIcon}>
                {isExpanded ? '−' : '+'}
              </div>
            </div>
          )}
        </div>

        {/* Thumbnail */}
        {!isMinimized && (
          <div style={styles.thumbnail}>
            <img 
              src={playlist[currentSongIndex].thumbnail} 
              alt={`${playlist[currentSongIndex].title} cover`}
              style={styles.thumbnailImage}
            />
            <div style={styles.thumbnailOverlay}>
              <div style={styles.songTitleMini}>{playlist[currentSongIndex].title}</div>
              <div style={styles.songArtistMini}>{playlist[currentSongIndex].artist}</div>
            </div>
          </div>
        )}

        {/* Expanded Content */}
        {isExpanded && !isMinimized && (
          <div style={styles.content}>
            {/* Error display */}
            {error && (
              <div style={styles.errorMessage}>
                <span>{error}</span>
                <button style={styles.errorClose} onClick={clearError}>✕</button>
              </div>
            )}
            
            {/* Progress Bar */}
            <div style={styles.progressSection}>
              <div style={styles.timeDisplay}>
                <span>{formatTime(currentTime)}</span>
                <span>{formatTime(duration)}</span>
              </div>
              <div 
                ref={progressRef}
                style={styles.progressBar}
                onClick={handleProgressClick}
              >
                <div 
                  style={{
                    ...styles.progressFill,
                    width: `${(currentTime / duration) * 100}%`
                  }}
                />
              </div>
            </div>
            
            {/* Music controls */}
            <div style={styles.controls}>
              <button style={styles.controlBtn} onClick={prevSong} title="Previous">
                ⏮
              </button>
              
              <button
                style={{
                  ...styles.playButton,
                  opacity: isReady ? 1 : 0.5
                }}
                onClick={togglePlay}
                disabled={!isReady}
              >
                {isPlaying ? '⏸' : '▶'}
              </button>
              
              <button style={styles.controlBtn} onClick={nextSong} title="Next">
                ⏭
              </button>
            </div>
            
            {/* Volume */}
            <div style={styles.volumeSection}>
              <span>{isMuted ? '🔇' : '🔊'}</span>
              <input
                type="range"
                min="0"
                max="1"
                step="0.1"
                value={isMuted ? 0 : volume}
                onChange={handleVolumeChange}
                disabled={!isReady}
                style={styles.volumeSlider}
              />
              <span>{isMuted ? '0%' : `${Math.round(volume * 100)}%`}</span>
            </div>
            
            {/* Music info */}
            <div style={styles.musicInfo}>
              <div style={styles.songTitle}>{playlist[currentSongIndex].title}</div>
              <div style={styles.songArtist}>{playlist[currentSongIndex].artist}</div>
              <div style={styles.songStatus}>
                {error ? 'เกิดข้อผิดพลาด' : 
                 isReady ? (isPlaying ? 'กำลังเล่น ♪' : 'พร้อมเล่น ♪') : 'กำลังโหลด...'}
                {isMuted && ' (ปิดเสียง)'}
              </div>
            </div>
          </div>
        )}

        {/* Playlist */}
        {showPlaylist && !isMinimized && (
          <div style={styles.playlistPanel}>
            <div style={styles.playlistHeader}>
              <span>🎵 Playlist</span>
              <button style={styles.playlistClose} onClick={togglePlaylist}>✕</button>
            </div>
            <div style={styles.playlistSongs}>
              {playlist.map((song, index) => (
                <div 
                  key={song.id}
                  style={{
                    ...styles.playlistSong,
                    ...(index === currentSongIndex ? styles.currentSong : {})
                  }}
                  onClick={() => changeSong(index)}
                >
                  <img 
                    src={song.thumbnail} 
                    alt={song.title}
                    style={styles.playlistThumbnail}
                  />
                  <div style={styles.playlistInfo}>
                    <div style={styles.playlistTitle}>{song.title}</div>
                    <div style={styles.playlistArtist}>{song.artist}</div>
                  </div>
                  {index === currentSongIndex && (
                    <div style={styles.currentIndicator}>
                      <span>♪</span>
                    </div>
                  )}
                </div>
              ))}
            </div>
          </div>
        )}
      </div>
    </div>
  );
};
