import React from 'react';
import { motion } from 'framer-motion';
import './LoveMessage.css';

const LoveMessage = ({ message, onClose }) => {
  console.log('LoveMessage rendered with message:', message);
  
  return (
    <motion.div
      className="love-message-popup"
      initial={{ opacity: 0, scale: 0, y: 50 }}
      animate={{ opacity: 1, scale: 1, y: 0 }}
      exit={{ opacity: 0, scale: 0, y: -50 }}
      transition={{ 
        type: "spring", 
        stiffness: 300, 
        damping: 20,
        duration: 0.5
      }}
    >
      <motion.div
        className="message-heart"
        animate={{
          scale: [1, 1.2, 1],
          rotate: [0, 10, -10, 0]
        }}
        transition={{
          duration: 1.5,
          repeat: Infinity,
          ease: "easeInOut"
        }}
      >
        ðŸ’–
      </motion.div>

      <motion.p
        className="message-text"
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ delay: 0.3, duration: 0.5 }}
      >
        {message}
      </motion.p>

      <motion.button
        className="close-button"
        onClick={onClose}
        whileHover={{ scale: 1.1 }}
        whileTap={{ scale: 0.9 }}
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ delay: 0.5, duration: 0.5 }}
      >
        à¸›à¸´à¸”
      </motion.button>

      {/* Floating hearts around the message */}
      {[...Array(6)].map((_, i) => (
        <motion.div
          key={i}
          className="message-floating-heart"
          initial={{ 
            opacity: 0,
            scale: 0,
            x: 0,
            y: 0
          }}
          animate={{ 
            opacity: [0, 1, 0],
            scale: [0, 1, 0],
            x: Math.cos(i * 60 * Math.PI / 180) * 80,
            y: Math.sin(i * 60 * Math.PI / 180) * 80
          }}
          transition={{
            duration: 2,
            repeat: Infinity,
            delay: i * 0.2,
            ease: "easeInOut"
          }}
        >
          {['ðŸ’–', 'ðŸ’•', 'ðŸ’—', 'ðŸ’“', 'ðŸ’˜', 'ðŸ’ž'][i % 6]}
        </motion.div>
      ))}
    </motion.div>
  );
};

export default LoveMessage;
