import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion';
import './LoveMessage.css';

const LoveMessage = ({ message, onClose }) => {
  const [randomPosition, setRandomPosition] = useState({ x: 0, y: 0 });
  
  useEffect(() => {
    // ‡∏™‡∏£‡πâ‡∏≤‡∏á‡∏ï‡∏≥‡πÅ‡∏´‡∏ô‡πà‡∏á random ‡∏ó‡∏µ‡πà‡∏´‡∏•‡∏≤‡∏Å‡∏´‡∏•‡∏≤‡∏¢‡∏°‡∏≤‡∏Å‡∏Ç‡∏∂‡πâ‡∏ô
    const generateRandomPosition = () => {
      // ‡∏™‡∏∏‡πà‡∏°‡∏ï‡∏≥‡πÅ‡∏´‡∏ô‡πà‡∏á‡πÅ‡∏ö‡∏ö‡∏Å‡∏£‡∏∞‡∏à‡∏≤‡∏¢‡∏ó‡∏±‡πà‡∏ß‡∏´‡∏ô‡πâ‡∏≤‡∏à‡∏≠
      const screenWidth = window.innerWidth;
      const screenHeight = window.innerHeight;
      
      // ‡∏™‡∏∏‡πà‡∏°‡∏ï‡∏≥‡πÅ‡∏´‡∏ô‡πà‡∏á x ‡πÅ‡∏•‡∏∞ y ‡πÅ‡∏ö‡∏ö‡∏Å‡∏£‡∏∞‡∏à‡∏≤‡∏¢‡∏ó‡∏±‡πà‡∏ß‡∏´‡∏ô‡πâ‡∏≤‡∏à‡∏≠
      const x = Math.random() * (screenWidth - 300); // ‡∏´‡πà‡∏≤‡∏á‡∏Ç‡∏≠‡∏ö‡∏ã‡πâ‡∏≤‡∏¢ 300px
      const y = Math.random() * (screenHeight - 200); // ‡∏´‡πà‡∏≤‡∏á‡∏Ç‡∏≠‡∏ö‡∏ö‡∏ô 200px
      
      // ‡∏ï‡∏£‡∏ß‡∏à‡∏™‡∏≠‡∏ö‡∏ß‡πà‡∏≤‡πÑ‡∏°‡πà‡πÉ‡∏´‡πâ popup ‡∏≠‡∏¢‡∏π‡πà‡∏Ç‡∏≠‡∏ö‡∏à‡∏≠‡πÄ‡∏Å‡∏¥‡∏ô‡πÑ‡∏õ
      const safeX = Math.max(50, Math.min(x, screenWidth - 350));
      const safeY = Math.max(50, Math.min(y, screenHeight - 250));
      
      return { x: safeX, y: safeY };
    };
    
    setRandomPosition(generateRandomPosition());
    console.log('Random position:', randomPosition);
  }, [message]); // ‡πÄ‡∏õ‡∏•‡∏µ‡πà‡∏¢‡∏ô‡∏ï‡∏≥‡πÅ‡∏´‡∏ô‡πà‡∏á‡∏ó‡∏∏‡∏Å‡∏Ñ‡∏£‡∏±‡πâ‡∏á‡∏ó‡∏µ‡πà message ‡πÄ‡∏õ‡∏•‡∏µ‡πà‡∏¢‡∏ô

  console.log('LoveMessage rendered with message:', message);
  
  return (
    <motion.div
      className="love-message-popup"
      initial={{ opacity: 0, scale: 0 }}
      animate={{ 
        opacity: 1, 
        scale: 1
      }}
      exit={{ opacity: 0, scale: 0 }}
      transition={{ 
        type: "spring", 
        stiffness: 300, 
        damping: 20,
        duration: 0.5
      }}
      style={{
        position: 'fixed',
        top: `${randomPosition.y}px`,
        left: `${randomPosition.x}px`,
        transform: 'none' // ‡πÑ‡∏°‡πà‡πÉ‡∏ä‡πâ transform ‡πÅ‡∏ö‡∏ö‡πÄ‡∏î‡∏¥‡∏°
      }}
    >
      <motion.div
        className="message-heart"
        animate={{
          scale: [1, 1.2, 1],
          rotate: [0, 10, -10, 0]
        }}
        transition={{
          duration: 1.5,
          repeat: Infinity,
          ease: "easeInOut"
        }}
      >
        üíñ
      </motion.div>

      <motion.p
        className="message-text"
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ delay: 0.3, duration: 0.5 }}
      >
        {message}
      </motion.p>

      <motion.button
        className="close-button"
        onClick={onClose}
        whileHover={{ scale: 1.1 }}
        whileTap={{ scale: 0.9 }}
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ delay: 0.5, duration: 0.5 }}
      >
        ‡∏õ‡∏¥‡∏î
      </motion.button>

      {/* Floating hearts around the message */}
      {[...Array(6)].map((_, i) => (
        <motion.div
          key={i}
          className="message-floating-heart"
          initial={{ 
            opacity: 0,
            scale: 0,
            x: 0,
            y: 0
          }}
          animate={{ 
            opacity: [0, 1, 0],
            scale: [0, 1, 0],
            x: Math.cos(i * 60 * Math.PI / 180) * 80,
            y: Math.sin(i * 60 * Math.PI / 180) * 80
          }}
          transition={{
            duration: 2,
            repeat: Infinity,
            delay: i * 0.2,
            ease: "easeInOut"
          }}
        >
          {['üíñ', 'üíï', 'üíó', 'üíì', 'üíò', 'üíû'][i % 6]}
        </motion.div>
      ))}
    </motion.div>
  );
};

export default LoveMessage;
